import React ,{Component} from 'react'

export default class IssuePage extends Component{
    constructor(props){
        super(props);
        this.state = { 
            issue:[],
            backendServer: 'http://localhost:8080/',                        
            id:this.props.match.params.id,
        }        
        
    };
    componentDidMount(){
        fetch(this.state.backendServer +"issue/"+ this.state.id)
        .then(response=>response.json())
        .then((data) => {
            this.setState({
                issue:data,
            })
        })
        .catch(console.log)
    }
    render(){
        console.log(this.state)
        return(
            <div className="issueDisplay">                
                 {this.state.issue.map((issue) => (
                     <div className="card">
                        <form>
                            <div className="form-group">
                                <h6 className="card-header"><input  type="text" value={issue.issueTitle}></input></h6>
                                <div className="card-body">
                                    
                                </div>                            
                            </div>
                        </form>
                     </div>
                 ))}
            </div>
        )
    }
}
