import React, {Component} from 'react'
import { runInThisContext } from 'vm';

export default class Login extends Component{
    constructor(props){
        super(props);
        this.state = {
            id_input:"",
            pw_input:"",
            ID:"",
            Name:"",
            Photo:"",
            backendServer : 'http://localhost:8080/'
        }
    }
    
    handleIdChange(e){
        //this.setState({id_input:e.target.value})
        console.log(e.target.value)
    }

    handlePwChange(e){
        this.setState({pw_input:e.target.value})
    }


    login(event){
        event.preventDefault();
        this.id= "EMP00001";
        this.password = "password";
        console.log(event.target)

        fetch(this.state.backendServer+"user/"+this.id+"&"+this.password)
        .then(response => response.json())
        .then((data)=>{
            this.setState({
                ID:data[0].ID,
                Name:data[0].Name,
                Photo:data[0].Photo
            }) 
                      
        })
        
        
    }


    render(){
        return(
            <div className="Login-Form">
                <form>
                    <div className="form-group">
                        <label>ID</label>
                        <br></br>
                        <input type="text" placeholder="EMP00001" className="id"  value = {this.state.id_input} onChange={this.handleIdChange}></input>
                    </div>
                    
                    <div className="form-group">
                        <label>Password</label>
                        <br></br>
                        <input type="password" className="pw" value = {this.state.pw_input} onChange={this.handlePwChange}></input>
                    </div>
                    <button type="submit" className="btn btn-info" onClick={(event) => this.login(event)}>Login</button>
                </form>
                <p>ID----{this.state.ID}</p>
                <img src={this.state.Photo}></img>

            </div>
            
        )
    }
}
