import React,{Component} from 'react';
import IssueList from './Components/issuesList';
import IssuePage from './Components/issuePage';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import Login from './Components/login'
import './App.css';

export default class App extends Component {
    constructor() {
      super();
        this.state = {
          logged:false,
          ID:"",
          Name:"",
          Photo:""
        };
    }

    loginUpdate = (loginData) => {
      console.log(loginData);
    }

    render()
    {
    return (
        <div className="App">
          <h4>Hello from App</h4>
          <h4>Name : {this.state.Name}</h4>
          <img src={this.state.Photo} alt=""></img>
          <Login UserInfo ={this.loginUpdate}></Login>
          <Router>
            <Switch>
              <Route exact path="/"><IssueList></IssueList></Route>
              <Route path="/issue/:id" component={IssuePage}></Route>
            </Switch>
          </Router>
        </div>
      );
    }
}
