import React, {Component} from 'react'

export default class Login extends Component{
    constructor(props){
        super(props);
        this.state = {
            ID:"",
            Name:"",
            Photo:"",
            backendServer : 'http://localhost:8080/'
        }
    }
    
    login(id,password){
        fetch(this.state.backendServer+"user/"+id+"&"+password)
        .then(response => response.json())
        .then((data)=>{
            this.setState({
                ID:data.ID,
                Name:data.Name,
                Photo:data.Photo
            })
        })
        .then(console.log(this.state))
        
    }


    render(){
        return(
            <div className="Login-Form">
                <form>
                    <div className="form-group">
                        <label>ID</label>
                        <br></br>
                        <input type="text" placeholder="EMP00001" className="id"></input>
                    </div>
                    
                    <div className="form-group">
                        <label>Password</label>
                        <br></br>
                        <input type="password" className="pw"></input>
                    </div>
                    <button className="btn btn-info" onClick={this.login("EMP00001","password")}>Login</button>
                </form>

            </div>
        )
    }
}
