import React, {Component} from 'react'
import { runInThisContext } from 'vm';

export default class Login extends Component{
    constructor(props){
        super(props);
        this.state = {
            ID:"",
            Name:"",
            Photo:"",
            backendServer : 'http://localhost:8080/'
        }
    }
    
    login(event){
        event.preventDefault();
        this.id= "EMP00001";
        this.password = "password";
        console.log("login")

        fetch(this.state.backendServer+"user/"+this.id+"&"+this.password)
        .then(response => response.json())
        .then((data)=>{
            this.setState({
                ID:data[0].ID,
                Name:data[0].Name,
                Photo:data[0].Photo
            }) 
            console.log(this.state);           
        })
        
        
    }


    render(){
        return(
            <div className="Login-Form">
                <form>
                    <div className="form-group">
                        <label>ID</label>
                        <br></br>
                        <input type="text" placeholder="EMP00001" className="id"></input>
                    </div>
                    
                    <div className="form-group">
                        <label>Password</label>
                        <br></br>
                        <input type="password" className="pw"></input>
                    </div>
                    <button className="btn btn-info" onClick={(event) => this.login(event)}>Login</button>
                </form>
                <p>ID----{this.state.ID}</p>

            </div>
            
        )
    }
}
