import React, {Component} from 'react'



class IssueList extends Component {

   

    constructor(props){
        super(props);

         

        this.state = {
            issuedata:[],
            backendServer : 'http://localhost:8080/'
        };

        
    }

    componentDidMount(){
        fetch(this.state.backendServer+ 'issuesOpen')
        .then(response => response.json())
        .then((data) => {
            this.setState({issuedata:data})
        })
        .catch(console.log)
    }

    
    render(){
        const highPriority = <div className="fa fa-exclamation-circle"><p class="font-weight-light text-capitalize">High</p></div>
         const bugOpen = <div className="fa fa-bug"></div>
         const bugClosed = <div className="fa fa-check-circle"></div>
        return(
           
            <div className="issueList">                
                {this.state.issuedata.map((issue) => (
                    <div className="card text-center">
                        <div className="card-title">
                            {issue.issueId}
                        </div>
                        <div className="card-body">
                            <h6>{issue.issueTitle}</h6>  
                            <div className="assignee_photo">
                                <img src={issue.assignee_photo} alt=""></img>
                            </div>                
                            {issue.priority == "High" ? highPriority:""}                            
                        </div>
                            <ul class="list-group list-group-flush">
                                <li class="list-group-item">{issue.status == "Open" ? bugOpen:bugClosed}  </li>                           
                            </ul>                        
                    </div>
                ))}                 
            </div>
          
        )
    }
}

export default IssueList;
