import React ,{Component} from 'react'

export default class IssuePage extends Component{
    constructor(props){
        super(props);
        this.state = { 
            issue:[],
            backendServer: 'http://localhost:8080/',                        
            id:this.props.match.params.id,
        }        
        
    };
    componentDidMount(){
        fetch(this.state.backendServer +"issue/"+ this.state.id)
        .then(response=>response.json())
        .then((data) => {
            this.setState({
                issue:data,
            })
        })
        .catch(console.log)
    }
    render(){
        console.log(this.state)
        return(
            <div class="card issueDisplay">
                 {this.state.issue.map((issue) => (
                     <form>
                        <div className="form-group">
                            <h5 className="card-header">{issue.issueId} : {issue.issueTitle}</h5>
                            
                        </div>
                     </form>

                 ))}
            </div>
        )
    }
}
