import React,{Component} from 'react';
import IssueList from './Components/issuesList';
import IssuePage from './Components/issuePage';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import Login from './Components/login'
import './App.css';

export default class App extends Component {
    constructor() {
      super();
        this.state = {
          logged:false,
          ID:"",
          Name:"",
          Photo:""
        };

    }

    loginUpdate = (loginData) => {
      this.setState({
        ID:loginData.ID,
        Name:loginData.Name,
        Photo:loginData.Photo,
        logged:loginData.logged
      })
      localStorage.setItem("logged",this.state.logged)
      localStorage.setItem("Name",this.state.Name)
      localStorage.setItem("ID",this.state.ID)
      localStorage.setItem("Photo",this.state.Photo)

      console.log(this.state)
      console.log(localStorage.getItem("logged"))

    }



    render()
    {
    return (
        <div className="App">
         <h4>Hello from App {localStorage.getItem("Name")} {localStorage.getItem("logged")}</h4>
          {!localStorage.getItem("logged")===true?
          <Login UserInfo ={this.loginUpdate}></Login>
          :
          <Router>
            <Switch>
              <Route exact path="/"><IssueList></IssueList></Route>
              <Route path="/issue/:id" component={IssuePage}></Route>
            </Switch>
          </Router>
          }
        </div>
      );
    }
}
