import React, {Component} from 'react'

export default class SearchUserIssue extends Component{
    constructor(props){
        super(props);
        this.state = {
            searchId:"",
            backendServer: 'http://localhost:8080/',
            userData:[]
        }
    }

    searchEmployee(event){
        event.preve
        fetch(this.state.backendServer+ "affectedUser/"+this.state.searchId)
        .then(response=>response.json())
        .then((data)=>{
            this.setState({
                userData:data
            })
        })
        .catch(console.log)
    }

    handleChangeEmployeeID(event){
        this.setState({
            searchId:event.target.value
        })

    }



    render(){
        return(
            <div className="searchBox">
                
                <form class="form">
                    <div className="container">
                        <div className="row">
                            <div class="col-md">
                                <input className="form-control" type="text" placeholder="Search" aria-label="Search" value={this.state.searchId}
                                onChange={(event)=>this.handleChangeEmployeeID(event)}/>
                            </div>
                            <div class="col-md-auto">
                                <i class="fa fa-search" aria-hidden="true"></i>
                            </div>                           
                        </div>
                    </div>
                    <br/>   
                    <button className="btn btn-info btn-sm my-0" type="submit" onClick={this.searchEmployee}>Search Employee </button>                 
                </form>
                

              
            </div>
        )
    }
}
